        color: #fff;
        text-align: center;
    }

    .not-found {
        width: 560px;
        height: 225px;
        margin-right: -10px;
    }

    .starry-sky {
        display: block;
        width: 100vw;
        height: 100vh;
        object-fit: cover;
    }

    .search-icon {
        display: inline-block;
    }

    .notfound-copy {
        color: #fff;
        position: fixed;
        top: 25px;
        right: 10%;
        text-align: right;
    }

    h1 {
        font-weight: 700;
        font-size: 40px;
    }

    a {
        font-weight: 300;
        color: #fff;
        text-decoration: none;
    }

    a:hover {
        font-weight: 300;
        color: #fff;
        text-decoration: none;
    }

    .all-stars {
        animation: blinkblink 7s linear infinite;
    }

    .btn-back {
        padding: 15px 20px;
        border: 1.5px solid #ffffff;
        background-color: transparent;
        color: #ffffff;
        cursor: pointer;
        font-size: 15px;
    }

    @keyframes blinkblink {
        50% {
            opacity: 0.1;
        }
    }

    .moon {}

    input[type=text] {
